deinitialize.body:
this.Scene.unbind('pointerMove', this.mousemove);
    this.Scene.unbind('pointerDown', this.mousepress);
    this.Scene.unbind('pointerUp', this.mouseup);
    this.Scene.unbind('pointerWheel', this.mousewheel);
    //This function was created for you by the system. 
    //Deinitialize is called when the object is being destroyed.
    // Clean up here if your object allocated any resources manually during initialize.

ready.body:
this.Scene.unbind('pointerMove', this.mousemove);
    this.Scene.unbind('pointerDown', this.mousepress);
    this.Scene.unbind('pointerUp', this.mouseup);
    this.Scene.unbind('pointerWheel', this.mousewheel);
    var self = this;
    this.mousemove = function(e, d)
    {

        var rel_x = self.last_x - e.position[0];
        var rel_y = self.last_y - e.position[1];
        self.rotateCam(rel_x * 3, -rel_y * 3);
        self.last_x = e.position[0];
        self.last_y = e.position[1];
    }
    this.mousewheel = function(e, d)
    {
        self.zoom += e.wheelDelta;
    }
    this.mousepress = function(e, d)
    {

        self.mousedown = true;
        self.last_x = e.position[0];
        self.last_y = e.position[1];
    }
    this.mouseup = function(e, d)
    {
        self.mousedown = false;
    }

    this.Scene.bind('pointerMove', this.mousemove);
    this.Scene.bind('pointerDown', this.mousepress);
    this.Scene.bind('pointerUp', this.mouseup);
    this.Scene.bind('pointerWheel', this.mousewheel);
    //This function was created for you by the system. 
    //The scene is now completely loaded. This will fire on each client when the client joins, so it`s not a great place to create objects

rotateCam.body:
var xrot = Quaternion.fromAngleAxis(rel_x, [0, 0, 1], []);
    var offset = this.offset.internal_val;
    offset = Mat4.multVec3NoTranslate(Quaternion.toRotationMatrix4(xrot, []), offset, []);

    var side = Vec3.cross(offset,[0,0,1],[]);
    var yrot = Quaternion.fromAngleAxis(rel_y,side,[]);
    offset = Mat4.multVec3NoTranslate(Quaternion.toRotationMatrix4(yrot, []), offset, []);
     this.offset = offset;

tick.body:
var node = this.Scene.findNodeByID(this.objectToLookAt);
        var pos = [0, 0, 0];
        if (node)
            pos = node.transformAPI.localToGlobal([0, 0, 0]);
        var offset = this.offset.internal_val;
        offset = MATH.scaleVec3(offset, this.zoom);
        this.transformAPI.setPosition(MATH.addVec3(pos, offset));
        this.transformAPI.lookat(pos, '', '-Y', 'Z');
